import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Restaurant from '../../Restaurants/Restaurant/Restaurant'
import firebase from '../../../axios-restaurants';
import styles from '../../Restaurants/Restaurants.module.css'

const Visit = () => {
    const [visits, setVisits] = useState([])
    const [loading,setLoading] = useState(false)
    const deleteBusiness = (index) => {
        const newBus = [...visits];
        newBus.splice(index, 1);
        setVisits(newBus)
    }

    const updateVisited = (newBus,index) => {
        deleteBusiness(index)
        firebase.post('/visited.json',{id: newBus.id})
            .catch(err => console.log(err))
    }

    useEffect(() => {
        let visitIds = []
        async function getVisits() {
            setLoading(true)
            axios.get('https://local-byte.firebaseio.com/visit.json')
            .then(res => {
                console.log(res)
                const visitIdObjs = Object.keys(res.data)
                visitIds = visitIdObjs.map((key) => {
                    return res.data[key].id
                })
                console.log(visitIds)
                setLoading(false)
        })}
        function getRestaurants() {
            const yelpBus = []
            visits.forEach(async (id)=>{
                await axios.get('https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/'+id, {
                    headers: {
                        Authorization: `Bearer ${process.env.REACT_APP_API_KEY}`
                    }

                }).then((res) => {
                    console.log('[RES]',res)
                    yelpBus.push(res.data.business)

                }).catch((err) => {
                    console.log(err)
                })})
            setLoading(false);
            setVisits(yelpBus)
        }
        getVisits();
        if (visits) {
            getRestaurants()
        }
    },[])

    let returnedRes = []
    if (visits) {
        returnedRes = visits.map((business,index) => {
            return <Restaurant 
            visited={() => updateVisited(business,index)}
            key={business.id}
            name={business.name}
            address={business.location.address1}
            city={business.location.city}
            state={business.location.state}
            stars={business.rating}
            categories={business.categories}
            phone={business.display_phone}
            pic={business.image_url}
        />
        })
    }

    return (
        <div className={styles.Restaurants}>
            {loading ? <p>Loading ...</p> : returnedRes}
        </div>
    )
}

export default Visit;
