import React from 'react';
import styles from './Restaurant.module.css';
import Ratings from 'react-ratings-declarative';

const restaurant = props => {          

    let hours = null
    if (props.hours) {
        hours = Object.keys(props.hours).map((day) => {
            return <p key={day}>{day}: {props.hours[day]}</p>
        })
    }

    let address = props.address;
    if (address) {
        address=address+','
    }
    return (
        <div 
            className={styles.Restaurant}>
            <p><strong>{props.name}</strong></p>
            <p style={{color: 'grey'}}>{props.categories}</p>
            <p>{address} {props.city}</p>
            <Ratings 
                rating={props.stars}
                widgetRatedColors="red">
                <Ratings.Widget
                    widgetDimension="10px"
                    widgetSpacing="5px"/>
                <Ratings.Widget
                    widgetDimension="10px"
                    widgetSpacing="5px"/>
                <Ratings.Widget
                    widgetDimension="10px"
                    widgetSpacing="5px"/> 
                <Ratings.Widget
                    widgetDimension="10px"/>
                <Ratings.Widget
                    widgetDimension="10px"/>                               
            </Ratings>
            <p>{props.phone}</p>
            {/* {hours} */}
            <button 
                className={styles.Button}
                onClick={props.visit}>Visit</button>
            <button 
                className={styles.Button}
                onClick={props.visited}>Visited</button>
        </div>
    )
}

export default restaurant;
