import React, { useEffect, useState, Fragment } from 'react';
import axios from 'axios';
import Restaurant from './Restaurant/Restaurant'
import firebase from '../../axios-restaurants';
import styles from './Restaurants.module.css'
import Autocomplete from 'react-google-autocomplete'
import Categories from '../Categories/Categories'

const Restaurants = () => {

    const [businesses, setBusinesses] = useState([])
    const [loading,setLoading] = useState(false)
    const [searchLocation, setSearchLocation] = useState(null)
    const [categories, setCategories] = useState([])
    const [visits, setVisits] = useState([])
    const [visiteds, setVisiteds] = useState([])

    const deleteBusiness = (index) => {
        const newBus = [...businesses];
        newBus.splice(index, 1);
        setBusinesses(newBus)
    }

    const updateVisited = (newBus,index) => {
        deleteBusiness(index)
        firebase.post('/visited.json',{id: newBus.id})
            .catch(err => console.log(err))
    }

    const updateVisit = (newBus,index) => {
        deleteBusiness(index)
        firebase.post('/visit.json',{id:newBus.id})
            .catch(err => console.log(err))
    }


    useEffect(() => {
        async function getRestaurants() {
            await axios.get("https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search", {
                headers: {
                    Authorization: `Bearer ${process.env.REACT_APP_API_KEY}`
                }, params: {        
                    location: searchLocation,
                    categories: categories.join(',')
                }, 

            }).then((res) => {
                setLoading(false)
                setBusinesses(res.data.businesses)

            }).catch((err) => {
                console.log(err)
            })
        }
        if (searchLocation){
            setLoading(true)
            getRestaurants()
        }

    }, [searchLocation,categories]);

    useEffect(() => {
        setLoading(true)
        axios.get('https://local-byte.firebaseio.com/visit.json')
        .then(userVisit => {
            const visitIdObjs = Object.keys(userVisit.data)
            const visitIds = visitIdObjs.map((key) => {
                return userVisit.data[key].id
            })
            const promises = visitIds.map(async (id) => {
                const res = await axios.get('https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/' + id, {
                    headers: {
                        Authorization: `Bearer ${process.env.REACT_APP_API_KEY}`
                    }
                });
                return res.data;})
            Promise.all(promises).then((restaurants) => {
                setVisits(restaurants) 
            })}).catch((err) => console.log(err))
            setLoading(false)
            }
    ,[visits])

    useEffect(() => {
        setLoading(true)
        axios.get('https://local-byte.firebaseio.com/visited.json')
        .then(userVisited => {
            if (!userVisited.data) {
                return
            }
            const visitedIdObjs = Object.keys(userVisited.data)
            const visitedIds = visitedIdObjs.map((key) => {
                return userVisited.data[key].id
            })
            const promises = visitedIds.map(async (id) => {
                const res = await axios.get('https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/' + id, {
                    headers: {
                        Authorization: `Bearer ${process.env.REACT_APP_API_KEY}`
                    }
                });
                return res.data;})
            Promise.all(promises).then((restaurants) => {
                setVisiteds(restaurants) 
            })}).catch((err) => console.log(err))
        setLoading(false)
        }
    ,[visiteds])

    const returnedRes = businesses.map((business, index) => {
        return <Restaurant 
            visit={() => updateVisit(business,index)}
            visited={() => updateVisited(business,index)}
            key={business.id}
            name={business.name}
            address={business.location.address1}
            city={business.location.city}
            state={business.location.state}
            stars={business.rating}
            categories={business.categories}
            phone={business.display_phone}
            pic={business.image_url}
        />
    })

    console.log(visit, visited)
    function categorySelected(category, selected) {
        console.log(category, selected)
        if (selected) {
            const oldSelected = [...categories]
            oldSelected.push(category)
            setCategories(oldSelected)
        } else {
            const newSelected = categories.filter((el) => {
                return category!==el
            })
            setCategories(newSelected)
        }
    }

    const possibleCat = ['mexican', 'chinese', 'indian', 'burgers', 
        'pizza', 'thai', 'japanese', 'mediterranean', 'mideastern',
        'breakfast_brunch','brazilian','cafes','caribbean','creperies',
        'delis','diners','latin','noodles','sandwiches','polish',
        'portuguese','seafood','soulfood','steak','sushi','vegetarian',
        'waffles','wraps']

    return (
        <Fragment>
            <Autocomplete
            onPlaceSelected={(place) => setSearchLocation(place.formatted_address)}
            className={styles.Search}/>
            <header style={{textAlign: 'left', fontSize: 'large', marginLeft: '3%'}}>Categories</header>
            <Categories
                categories={possibleCat}
                checked={categorySelected}/>
            <div className = {styles.Restaurants}
                style={{marginTop:'5px'}}>
                {loading ? <p>Loading ...</p> : returnedRes}
            </div>
        </Fragment>
    )
}

export default Restaurants;
