import React, { useState } from 'react';
import styles from './Categories.module.css';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';

const Categories = props => {

    const [checkedCat, setCheckedCat] = useState({})

    let initalizedCheckedCat = {}    
    for (category of props.categories) {
        initalizedCheckedCat.category = false;
    }

    setCheckedCat(initalizedCheckedCat)

    function updateChecked(category) {
        const oldChecked = {...checkedCat}
        oldChecked[category] = !checkedCat[category]
        setCheckedCat(oldChecked)
    }

    const checkboxes = props.categories.map((cat,index) => {
        return (
            <FormControlLabel
                key={cat}
                label={cat}
                control={<Checkbox onChange={()=> {
                    updateChecked(cat)
                    props.checked(cat,checkedCat[cat],index
                )}}/>
            }/>)})

    return (
        <div className={styles.Categories}>
            <header>Categories</header>
            {checkboxes}
        </div>
    )
}

export default Categories;
